mapper.select:
SELECT r.id, r.name, r.description, r.created_at, r.updated_at, r.role_created_by FROM roles r WHERE 1=1
AND LOWER(r.name) LIKE LOWER(CONCAT('%', #{roleName}, '%'))
AND r.created_at >= #{startDate}
AND r.created_at <= #{endDate}
AND r.role_created_by = #{roleCreatedBy}
AND ( LOWER(r.name) LIKE LOWER(CONCAT('%', #{searchTerm}, '%')) OR LOWER(r.description) LIKE LOWER(CONCAT('%', #{searchTerm}, '%')) OR LOWER(r.role_created_by) LIKE LOWER(CONCAT('%', #{searchTerm}, '%')) )
ORDER BY
r.name r.description r.created_at r.updated_at r.created_at
ASC DESC

SELECT COUNT(*) FROM roles r
WHERE 1=1


    AND LOWER(r.name) LIKE LOWER(CONCAT('%', #{roleName}, '%'))



    AND r.created_at >= #{startDate}



    AND r.created_at <= #{endDate}



    AND r.role_created_by = #{roleCreatedBy}



    AND (
    LOWER(r.name) LIKE LOWER(CONCAT('%', #{searchTerm}, '%'))
    OR LOWER(r.description) LIKE LOWER(CONCAT('%', #{searchTerm}, '%'))
    OR LOWER(r.role_created_by) LIKE LOWER(CONCAT('%', #{searchTerm}, '%'))
    )

mapper.insert:
INSERT INTO roles (name, description, created_at, updated_at, role_created_by)
VALUES (#{name}, #{description}, #{createdAt}, #{updatedAt}, #{roleCreatedBy})
    RETURNING id;

mapper.update:
UPDATE roles
SET name = #{name},
description = #{description},
updated_at = #{updatedAt},
role_created_by = #{roleCreatedBy}
WHERE id = #{id}
